import React, { useState } from 'react'

const Statistics = (props) =>
{
  if (props.counter === 0){
    return(
      <h3>no data</h3>
    )
  }
  return(
    <>
    good {props.good} <br></br>
    neutral {props.neutral}<br></br>
    bad {props.bad} <br></br>
    average {props.average} <br></br>
    positive {props.positive}%<br></br>
    </>
  )
}

const App = () => {
  const [good, setGood] = useState(0)
  const [neutral, setNeutral] = useState(0)
  const [bad, setBad] = useState(0)
  const [average, setAverage] = useState(0)
  const [counter, setCounter] = useState(0)

  const goodFeedback = () => {
    setGood(good + 1)
    setAverage(average + 1)
    setCounter(counter + 1)
  }

  const neutralFeedback = () => {
    setNeutral(neutral + 1)
    setCounter(counter + 1)
  }

  const badFeedback = () => {
    setBad(bad + 1)
    setAverage(average - 1)
    setCounter(counter + 1)
  }



  return (
    <>
    <h1>give feedback</h1>
    <Button />
    <button onClick={goodFeedback}>
        good
    </button>
    <button onClick={neutralFeedback}>
        neutral
    </button>
    <button onClick={badFeedback}>
        bad
    </button>
    <h1>statistics</h1>
    <Statistics good={good} counter={counter} neutral={neutral} bad={bad} average={average/counter} positive={good/counter * 100}/>
    </>
  )
}

export default App
